import React, { Component } from 'react';
import { Button, Slider, Form, Icon, Input, Radio, Select, DatePicker, Rate } from 'antd';

const FormItem = Form.Item;
const Option = Select.Option;
const RadioButton = Radio.Button;
const RadioGroup = Radio.Group;

const condition = {
  0: {
    style: {
      color: '#f50',
    },
    label: <strong>Very Bad</strong>,
  },
  1: '',
  2: '',
  3: '',
  4: '',
  5: '',
  6: {
    style: {
      color: '#050',
    },
    label: <strong>Very Good</strong>,
  },
};

class MedicalForm extends Component {

  render() {
    //const { getFieldDecorator } = this.props.form;
    const formItemLayout = {
      labelCol: { span: 8 },
      wrapperCol: { span: 12 },
    };
    return (
      <Form>
        <FormItem
          {...formItemLayout}
          label="How are you feeling today?"
        >
          <span className="bad">Bad</span> <Rate /> <span className="good">Good</span>
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="When is the last time you took Thalomid™"
        >
          <DatePicker />
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="When is the last time you took Revlimid™"
        >
          <DatePicker />
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Rate your condition compared to a week ago"
        >
        <span className="bad">Bad</span> <Rate /> <span className="good">Good</span>
        </FormItem>
        <FormItem
          {...formItemLayout}
        >
          <Button onClick={() => this.props.callback()} type="primary">Submit form</Button>
        </FormItem>
      </Form>
    );
  }

}

export default MedicalForm;
